import React, { useEffect, useState } from 'react';
import {
  ArrowTrendingUpIcon,
  ArrowTrendingDownIcon,
} from '@heroicons/react/24/outline';

const Dashboard = () => {
  const [metrics, setMetrics] = useState({
    total_trades: 0,
    win_rate: 0,
    profit_factor: 0,
    net_profit: 0,
  });

  const [algo_health, setAlgoHealth] = useState({
    health_score: 0,
    status: 'loading',
  });

  useEffect(() => {
    fetch('http://localhost:8000/metrics')
      .then((res) => res.json())
      .then((data) => setMetrics(data))
      .catch((err) => console.error('Metrics fetch error:', err));

    fetch('http://localhost:8000/algo-health')
      .then((res) => res.json())
      .then((data) => setAlgoHealth(data))
      .catch((err) => console.error('Algo health fetch error:', err));
  }, []);

  return (
    <div className="p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      <MetricCard
        title="Total Trades"
        value={metrics.total_trades}
        change={metrics.total_trades > 0 ? 'positive' : 'neutral'}
        icon={ArrowTrendingUpIcon}
      />

      <MetricCard
        title="Win Rate"
        value={`${metrics.win_rate}%`}
        change={metrics.win_rate >= 50 ? 'positive' : 'negative'}
        icon={metrics.win_rate >= 50 ? ArrowTrendingUpIcon : ArrowTrendingDownIcon}
      />

      <MetricCard
        title="Profit Factor"
        value={metrics.profit_factor}
        change={metrics.profit_factor >= 1.5 ? 'positive' : 'negative'}
        icon={metrics.profit_factor >= 1.5 ? ArrowTrendingUpIcon : ArrowTrendingDownIcon}
      />

      <MetricCard
        title="Net Profit"
        value={`$${metrics.net_profit}`}
        change={metrics.net_profit >= 0 ? 'positive' : 'negative'}
        icon={metrics.net_profit >= 0 ? ArrowTrendingUpIcon : ArrowTrendingDownIcon}
      />

      <MetricCard
        title="Health Score"
        value={`${algo_health.health_score}/100`}
        change={
          algo_health.health_score >= 80
            ? 'positive'
            : algo_health.health_score >= 60
            ? 'warning'
            : 'negative'
        }
        icon={
          algo_health.status === 'healthy'
            ? ArrowTrendingUpIcon
            : ArrowTrendingDownIcon
        }
      />
    </div>
  );
};

const MetricCard = ({ title, value, change, icon: Icon }) => {
  const changeColors = {
    positive: 'text-green-500',
    negative: 'text-red-500',
    warning: 'text-yellow-500',
    neutral: 'text-gray-500',
  };

  return (
    <div className="bg-white rounded-2xl shadow p-4 flex flex-col items-start">
      <div className="flex items-center justify-between w-full">
        <h3 className="text-sm font-medium text-gray-500">{title}</h3>
        <Icon className="h-6 w-6 text-gray-400" />
      </div>
      <p className="mt-2 text-2xl font-bold text-gray-900">{value}</p>
      <p className={`mt-1 text-sm ${changeColors[change] || 'text-gray-500'}`}>
        {change === 'positive'
          ? 'Improving'
          : change === 'negative'
          ? 'Declining'
          : change === 'warning'
          ? 'At Risk'
          : 'Stable'}
      </p>
    </div>
  );
};

export default Dashboard;
